let types = {
  default: {
    "is_wysiwyg_enabled": false,
    "is_html_allowed_on_front": true,
    "used_for_sort_by": false,
    "is_filterable": false,
    "is_filterable_in_search": false,
    "is_used_in_grid": false,
    "is_visible_in_grid": false,
    "is_filterable_in_grid": false,
    "is_searchable": "1",
    "is_visible_in_advanced_search": "1",
    "is_visible_on_front": "1",
    "used_in_product_listing": "1",
    "is_visible": true,
    "entity_type_id": "4",
    "is_required": false,
    "is_user_defined": true,
    "frontend_labels": [],
    "is_unique": "0"
  },
  //Number, Price, JSON, Text Area, Text Field, Yes/No
  number: {
    "frontend_input": "text",
    "backend_type": "varchar",
  },
  text: {
    "frontend_input": "text",
    "backend_type": "varchar",
  },
  textArea: {
    "frontend_input": "textarea",
    "backend_type": "text",
  },
  bool: {
    "frontend_input": "boolean",
    "backend_type": "int",
    "default_value": "0",
  },
  price: {
    "frontend_input": "price",
    "backend_type": "decimal",
  }
}

for (const attribute of $input.all()) {
  attribute.json.dto = {
    attribute_code: attribute.json["Attribute Name"].toLowerCase().replace(' ', '_'),
    default_frontend_label: attribute.json["Attribute Name"]
  }
  Object.assign(attribute.json.dto, types.default)
  switch(attribute.json["Attribute Type"]) {
    case "Text Field":
      Object.assign(attribute.json.dto, types.text)
    break;
    case "Price":
      Object.assign(attribute.json.dto, types.price)
    break;
    case "Number":
      Object.assign(attribute.json.dto, types.number)
    break;
    case "Text Area":
      Object.assign(attribute.json.dto, types.textArea)
    break;
    case "Yes/No":
      Object.assign(attribute.json.dto, types.bool)
    break;
    default:
      Object.assign(attribute.json.dto, types.text)
  }
}


return $input.all();
